import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Image } from 'react-native';

const CustomSwitch = () => {
  const [isOn, setIsOn] = useState(false);

  const toggleSwitch = () => {
    setIsOn(!isOn);
  };

  return (
    <TouchableOpacity style={styles.switch} onPress={toggleSwitch}>
      <View
        style={[
          styles.circle,
          isOn ? styles.circleOn : styles.circleOff,
        ]}
      >
        <Image
          style={styles.icon}
          source={{
            uri: isOn
              ? 'data:image/svg+xml;base64,'
              : 'data:image/svg+xml;base64,',
          }}
        />
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  switch: {
    width: 48,
    height: 20,
    backgroundColor: 'rgb(135, 150, 165)',
    borderRadius: 999,
    justifyContent: 'center',
    padding: 4,
  },
  circle: {
    width: 32,
    height: 32,
    borderRadius: 16,
    position: 'absolute',
    top: -6,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  circleOn: {
    left: 16,
    backgroundColor: 'rgb(0, 56, 146)',
  },
  circleOff: {
    left: 0,
    backgroundColor: 'rgb(0, 56, 146)',
  },
  icon: {
    width: 20,
    height: 20,
  },
});

export default CustomSwitch;
